value.stringValue:
IF(
    NOT(ISPICKVAL([Case].Status ,'New')) || (ISBLANK([Case].ParentId)),false,
    AND(
        NOT(ISBLANK([Case].ParentId)),
        OR(
            AND(
                [Case].Parent.RecordType.DeveloperName == 'DE_Inbound_Case', NOT([Case].Handover__c)
            ),
            AND(
                OR(
                    [Case].Parent.RecordType.DeveloperName == 'AT_Inbound_Case',
                    [Case].Parent.RecordType.DeveloperName == 'PY_Inbound_Case',
                    [Case].Parent.RecordType.DeveloperName == 'CD_Inbound_Case',
                    [Case].Parent.RecordType.DeveloperName == 'Talabat_Inbound_Case'
                ),
                CASE([Case].Parent.Type, 'Handover', 'false', 'true') ='true',
                OR(
                    NOT([Case].Parent.Escalate__c),
                    AND(
                        [Case].Parent.Escalate__c, [Case].Parent.Override_Validations__c, CASE([Case].Parent.Override_Validation_Reason__c,'Case Escalated To Sales Admin','true','false')='false'
                    )
                )
            )
        )
    )
)

formulas.expression:
IF(
    NOT(ISPICKVAL({!myVariable_current.Status} ,'New')) || (ISBLANK({!myVariable_current.ParentId})),false,
    AND(
        NOT(ISBLANK({!myVariable_current.ParentId})),
        OR(
            AND(
                {!myVariable_current.Parent.RecordType.DeveloperName} == 'DE_Inbound_Case', NOT({!myVariable_current.Handover__c})
            ),
            AND(
                OR(
                    {!myVariable_current.Parent.RecordType.DeveloperName} == 'AT_Inbound_Case',
                    {!myVariable_current.Parent.RecordType.DeveloperName} == 'PY_Inbound_Case',
                    {!myVariable_current.Parent.RecordType.DeveloperName} == 'CD_Inbound_Case',
                    {!myVariable_current.Parent.RecordType.DeveloperName} == 'Talabat_Inbound_Case'
                ),
                CASE({!myVariable_current.Parent.Type}, 'Handover', 'false', 'true') ='true',
                OR(
                    NOT({!myVariable_current.Parent.Escalate__c}),
                    AND(
                        {!myVariable_current.Parent.Escalate__c}, {!myVariable_current.Parent.Override_Validations__c}, CASE({!myVariable_current.Parent.Override_Validation_Reason__c},'Case Escalated To Sales Admin','true','false')='false'
                    )
                )
            )
        )
    )
)

