value.stringValue:
IF(ISBLANK([Case].ParentId ),false,
    OR(
        AND (
            [Case].Parent.RecordType.DeveloperName  = 'DE_Inbound_Case',
            [Case].Parent.Handover__c = False
        ),
        AND(
            CASE([Case].Parent.Type, 'Handover', 'false', 'true') ='true',
            AND(
                OR(
                    [Case].Parent.RecordType.DeveloperName  = 'CD_Inbound_Case',
                    [Case].Parent.RecordType.DeveloperName  = 'Talabat_Inbound_Case',
                    [Case].Parent.RecordType.DeveloperName  = 'PY_Inbound_Case',
                    [Case].Parent.RecordType.DeveloperName  = 'AT_Inbound_Case'
                ),
                OR(
                    [Case].Parent.Escalate__c = false,
                    AND(
                        [Case].Parent.Escalate__c = true,
                        OR(
                            [Case].Parent.Override_Validations__c = false,
                            AND(
                                [Case].Parent.Override_Validations__c = true,
                                CASE(([Case].Parent.Override_Validation_Reason__c),'Case Escalated To Sales Admin','true','false')='false'
                            )
                        )
                    )
                )
            ),
            OR(
            ISCHANGED([Case].Status ),	ISCHANGED([Case].ParentId)
            )
        )
    )
)

