var myquery = "SELECT Id, Type,Backend_ID__c FROM Account WHERE Id = '{!Account.Id}' limit 1"; 
result = sforce.connection.query(myquery); 
records = result.getArray("records"); 

if(records[0]) 
{ 
var atype="{!Account.Type}";  
var backend ="{!Account.Backend_ID__c}"; 
} 


if (typeof(srcUp) == 'function') {
	if ((atype=='Branch') && (backend != null)){
	srcUp('https://domicilios.com/super/establecimientos/edit/{!Account.Backend_ID__c}'); 
	} 
	else if((atype=='Client') && (backend != null)){
	srcUp('https://domicilios.com/facturacion/facturas/cliente/{!Account.Backend_ID__c}'); 
	}


}

else {
	if ((atype=='Branch') && (backend != null)){
	window.open('https://domicilios.com/super/establecimientos/edit/{!Account.Backend_ID__c}'); 
	} 
	else if((atype=='Client') && (backend != null)){
	window.open('https://domicilios.com/facturacion/facturas/cliente/{!Account.Backend_ID__c}'); 
	}
 }
