WebLink.url:
{!REQUIRESCRIPT("/support/console/38.0/integration.js")}

(function() {
	var url = "/apex/AccountHierarchy?id={!Account.Id}";

	if (sforce.console.isInConsole()) {
		sforce.console.getFocusedPrimaryTabId(function (res) {
			sforce.console.openSubtab(res.id, url, true, "Account Hierarchy");
		});
	} else {
		window.location.href = url;
	}

})();
