function requireCssFile(filename){ 
var fileref = document.createElement('link');
fileref.setAttribute('rel', 'stylesheet');
fileref.setAttribute('type', 'text/css');
fileref.setAttribute('href', filename);
document.getElementsByTagName('head')[0].appendChild(fileref);
}
{!REQUIRESCRIPT("/resource/bootbox/utilityFunctions.js")}



var callback = { onSuccess: function(resultData){
var result = ''+resultData;
debugger;

if(result.indexOf(':')> 0){
bootbox.alert(result, function() {
}).find('.modal-content').addClass( "error" );
}else{
sforce.console.openPrimaryTab(null, '/'+result, true);

sforce.console.getEnclosingTabId(closeSubtab);


}
}, onFailure: function(error){
bootbox.alert(error, function() {
}).find('.modal-content').addClass( "error" );
}};

sforce.apex.execute("ClsRemoteController","cloneAssetRemote",{assetId:"{!Asset__c.Id }"},callback );

var closeSubtab = function closeSubtab(result) {
            //Now that we have the tab ID, we can close it
            var tabId = result.id;
            sforce.console.closeTab(tabId, callback);
        };
