construct(this,"{!$Label.Asset_Terminating}");

var terminationdate = "{!Asset__c.Termination_Date__c}";

if(terminationdate!=null && terminationdate.length>0){
	bootbox.alert('{!$Label.Asset_terminated_already}').find('.modal-content').addClass( "info" );
	postAction();
}
else{

bootbox.confirm("{!$Label.Asset_terminate_confirmation}", function(userResult) {
if(userResult){
	var callback = { onSuccess: function(resultData){
	var result = ''+resultData;

	if(result.indexOf(':')> 0){
	bootbox.alert(result, function() {
		  refreshParent();
		}).find('.modal-content').addClass( "error" );
	}else{
		bootbox.alert(result, function() {
		  refreshParent();
		}).find('.modal-content').addClass( "success" );
	}
		}, onFailure: function(error){
		bootbox.alert(error, function() {
		 refreshParent();
		}).find('.modal-content').addClass( "error" );
		}};
	 sforce.apex.execute("ClsRemoteController","cancelAssetAdditionalCharges",{assetId:"{!Asset__c.Id }"},callback );

}else{
 	postAction();

}

}).find('.modal-content').addClass( "success" ); 


}
