ValidationRule.errorConditionFormula:
AND(
OR( TEXT( Type ) = "Menu Processing", TEXT( Type ) = "Menu Change" ), TEXT( Status ) = "Closed", ISBLANK( Menu_URL__c ), RecordType.Name = "FP Entry Case", OR( TEXT( Closed_Reason__c ) = "Take Online", TEXT( Closed_Reason__c ) = "Successful" ), NOT($Permission.Exclude_from_Validation)
)
