var btn = document.getElementsByName('back_to_queue')[0]; 
construct(btn,"{!$Label.Send_BacktoQueue}"); 


var callback = { onSuccess: function(resultData){ 
var result = ''+resultData; 

if(result == '{!$Label.Back_to_Queue_Closed_Case}' || result =='{!$Label.Level_1_Support_Queue}' || result == '{!$Label.Back_to_Queue_Unsucessful_AU}' || result=='{!$Label.BacktoQueue_owner_change}' ){ 
bootbox.alert(result, function() { 
refreshParent(); 
}).find('.modal-content').addClass( "error" ); 
}else{ 
bootbox.alert(result, function() { 
refreshParent(); 
}).find('.modal-content').addClass( "success" ); 
} 
}, onFailure: function(error){ 
bootbox.alert(error, function() { 
refreshParent(); 
}).find('.modal-content').addClass( "error" ); 
}}; 
var callResult = sforce.apex.execute("ClsRemoteController","backToQueue",{caseId:'{!Case.Id}'},callback);
