var btn = document.getElementsByName('mark_complete')[0];
construct(btn,"Marking complete");


var callback = { onSuccess: function(resultData){
var result = ''+resultData;

if(result != '{!$Label.Mark_Complete_Success}'){
bootbox.alert(result, function() {
refreshParent();
}).find('.modal-content').addClass( "error" );
}else{
//bootbox.alert(result, function() {
refreshParent();
//}).find('.modal-content').addClass( "success" );
}
}, onFailure: function(error){
bootbox.alert(error, function() {
refreshParent();
}).find('.modal-content').addClass( "error" );
}};
var callResult = sforce.apex.execute("ClsRemoteController","undoComplete",{caseId:'{!Case.Id}'},callback);
