{!REQUIRESCRIPT("/soap/ajax/20.0/apex.js")} 
{!REQUIRESCRIPT("/support/console/30.0/integration.js")} 

var leadId = "{!Lead.Id}";
var callResult = sforce.apex.execute("ClsLeadProcessor","processLeadConversion",{idOfLead:"{!Lead.Id}", companyCodeLead:"{!Lead.Company_Code__c}", future:true}); 

if(callResult[0] == 'success'){
   if(callResult[1] == 'showConversionPage'){
       if (sforce.console.isInConsole()) {

      }
      navigateToUrl('/apex/LeadConversion?id=' + leadId+'&amp;isdtp=vw','DETAIL');     
   }else{
      if (sforce.console.isInConsole()) {
        sforce.console.getEnclosingPrimaryTabId(showTab);
      }
   }
} else {
   alert(callResult[1]);
}

function showTab(result) {
  sforce.console.openPrimaryTab(result.id,'/' + callResult[1],true);
}
