{!REQUIRESCRIPT("/xdomain/xdomain.js")}
{!REQUIRESCRIPT("/soap/ajax/26.0/connection.js")}
{!REQUIRESCRIPT("/support/console/30.0/integration.js")} 

if("{!Opportunity.StageName}" != 'Closed Won'){
   alert("Opportunity is not Closed Won. Please complete the Quality Check");
}else{

if (sforce.console.isInConsole()) { 
    sforce.console.getFocusedPrimaryTabId(function(result){
         sforce.console.openSubtab(result.id , '/apex/ApplyChainOpportunityDeals?id={!Opportunity.Id}', true,
                'Apply Deals', null);
    });
}else{
   window.open("/apex/ApplyChainOpportunityDeals?id={!Opportunity.Id}","Apply Deal","width=1000,height=1000");
}
}
