WebLink.url:
{!REQUIRESCRIPT("/support/console/30.0/integration.js")} 
var url = "/apex/DownloadAllAttachments?id={!Opportunity.Id}&name={!Opportunity.Name}";
if (sforce.console.isInConsole()) {
	sforce.console.getEnclosingPrimaryTabId(function (res) {
		sforce.console.openSubtab(res.id, url, true, "Download Attachments");
	});
} else {
	window.open(url, "Download Attachments");
}
